parameters.jsCode:
// Extract casino information
const html = $input.first().json.data;
const cheerio = require('cheerio');
const $ = cheerio.load(html);

// Extract key casino data
const casinoData = {
  name: $('title').text() || $node.parameters.casino_name,
  license: $('.license, .licensing').text() || 'Not specified',
  games: $('.games-count, .game-providers').text() || 'Multiple providers',
  bonuses: $('.bonus, .welcome-bonus').text() || 'Welcome bonus available',
  paymentMethods: $('.payment-methods, .banking').text() || 'Multiple payment options',
  support: $('.support, .contact').text() || '24/7 support available',
  mobileOptimized: $('[class*="mobile"], [class*="responsive"]').length > 0,
  established: $('.established, .since').text() || 'Established operator',
  trustSignals: $('.ssl, .secure, .certified').length,
  extractedAt: new Date().toISOString()
};

return { casinoData };

// SEO optimization and quality checks
const content = $input.first().json.choices[0].text;

// Calculate SEO metrics
const wordCount = content.split(' ').length;
const headingCount = (content.match(/<h[1-6]/gi) || []).length;
const linkCount = (content.match(/<a /gi) || []).length;
const imageCount = (content.match(/<img /gi) || []).length;

// Quality scoring
const qualityScore = Math.min(10, 
  (wordCount > 1500 ? 3 : 0) +
  (headingCount > 5 ? 2 : 0) +
  (linkCount > 3 ? 2 : 0) +
  (content.includes('license') ? 1 : 0) +
  (content.includes('bonus') ? 1 : 0) +
  (content.includes('games') ? 1 : 0)
);

// SEO optimization
const seoScore = Math.min(10,
  (wordCount >= 2000 ? 3 : 0) +
  (headingCount >= 6 ? 2 : 0) +
  (content.includes('meta description') ? 2 : 0) +
  (linkCount >= 5 ? 2 : 0) +
  (content.includes('schema') ? 1 : 0)
);

return {
  content,
  wordCount,
  qualityScore,
  seoScore,
  status: qualityScore >= 7 ? 'approved' : 'needs_review'
};

